export default function Todo(props) {
    return (
        <div className="todo">
            <p>{props.todo.todo}</p>
            <div>
                <button
                    className="todo__status"
                    onClick={() => updateTodo(props.todo._id, props.todo.status)}
                >
                    {(props.todo.status) ? "☑" : "☐"}
                </button>
            </div>
        </div>
    )
  }
